import React, {useState} from 'react';

import style from "./Login.module.css";

import logo from "../../sourse/images/img_1.png";
import closeIcon from "../../sourse/images/close-icon.png";
import strel1 from "../../sourse/images/img_2.png";
import strel2 from "../../sourse/images/img_3.png";

const Login = (props) => {

    const [status, setStatus] = useState(props.status);

    const changing = () => {
        setStatus(!status);
    }

    const [login, setLogin] = useState('');
    const [password1, setPassword1] = useState('');
    const [password2, setPassword2] = useState('');

    return (
        <React.Fragment>
            <div className="w-full justify-center flex">
                <div className={style.logotype}>
                    <img src={logo} alt="logotype"/>
                </div>
            </div>

            <div className={style.form}>
                <div className={style.form__close}>
                    <div className={style.form__close_block}>
                        <img src={closeIcon} alt="closeIcon"/>
                    </div>
                </div>
                <div className={style.form__title}>
                    {status ? "Регистрация" : "Вход"}
                </div>
                <div className={style.form__grid}>
                    <img className={style.form__grid_first} src={strel2} alt="strel" onClick={changing}/>
                    <div className={style.form__grid__input}>
                        <div className={style.form__grid__input_t}>
                            <input type="text" placeholder="логин" onChange={(e) => {
                                setLogin("");
                                setLogin(e.target.value)
                            }}/>
                        </div>
                        <div className={style.form__grid__input_t}>
                            <input type="text" placeholder="пароль" onChange={(e) => {
                                setPassword1("");
                                setPassword1(e.target.value)
                            }}/>
                        </div>
                        {
                            status && <div className={style.form__grid__input_t}>
                                <input type="text" placeholder="повтор" onChange={(e) => {
                                    setPassword2("");
                                    setPassword2(e.target.value)
                                }}/>
                            </div>
                        }
                    </div>
                    <img className={style.form__grid_second} src={strel1} alt="strel" onClick={changing}/>
                </div>
                {
                    !status && <div className={style.form__remake}>
                        <div className={style.form__remake_title}>Забыли логин или пароль?</div>
                        <div className={style.form__remake_redtitle}>восстановить</div>
                    </div>
                }
                <div className={style.form__button}>
                    <div className={style.form__button_button} onClick={
                        console.log("логин - ", login, " ", "пароль - ", login, " ", "второй пароль - ", login, " ",)
                    }>
                        зарегистрироваться
                    </div>
                </div>

            </div>
        </React.Fragment>
    );
};

export default Login;
