import React, {Component} from "react";
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';

import logo from "../../sourse/images/new_лого.png";

import style from "./Header.module.css"
import {
    FORM_ROUTE,
    HOME_ROUTE,
    FEEDBACK_ROUTE,
    AUTHORISATION_ROUTE,
    ERROR_ROUTE
} from "../../utils/consts";

export default class Header extends Component {

    constructor(props) {
        super(props);
        this.state = {
            theme: props.theme,
            bodyClass: '',
            color: 'black'
        }
    }
    toDark = () => {
        this.setState({
            theme: 'dark',
            bodyClass: 'black',
            color: 'white'
        })
    }
    toLight = () => {
        this.setState({
            theme: 'light',
            bodyClass: '',
            color: 'black'
        })
    }
    render() {
        const { bodyClass, color } = this.state;

        return (
            <header style={
                {
                    background: bodyClass
                }
            } >
                <Link to={HOME_ROUTE}>
                    <img src={logo} className={style.navigation} alt={style.navigation}/>
                </Link>
                <nav className={style.navigation} >
                    <Link to={HOME_ROUTE} style={{color:color}}>Home</Link>
                    <Link to={FORM_ROUTE} style={{color:color}}>Form</Link>
                    <Link to={ERROR_ROUTE} style={{color:color}}>Services</Link>
                    <Link to={FEEDBACK_ROUTE} style={{color:color}}>Feedback</Link>
                </nav>
                <div>
                    <Button variant="light" className={'m-1'} onClick={this.toLight}>Light</Button>{' '}
                    <Button variant="dark" className={'m-lg-2'} onClick={this.toDark}>Dark</Button>
                    <Link to={AUTHORISATION_ROUTE}>
                        <button>Login</button>
                    </Link>
                </div>


            </header>
        );
    }
}
