import style from "./Home.module.css";
import {ERROR_ROUTE, LOGIN_ROUTE} from "../../utils/consts";
import {deleteCookie, getCookie, setCookie} from "../../utils/cookie";
import axios from "axios";
import Header from "../../components/Header/Header";
import Modal from "../../components/modal/Modal";
const Home = (props) => {

    if (getCookie("jwt")) {


        axios.get(
            "url",
            {headers: {
                    'Authorization': 'Bearer ' + getCookie("jwt") //the token is a variable which holds the token
                }}
        ).then(
            alert("пропуск")
        ).catch(
            err => {
                alert("есть но не совпал")
                deleteCookie("jwt")
                window.location.replace(LOGIN_ROUTE)
            }
        )


    const [modalActive, setModalActive] = useState(false)


    console.log(modalActive)

        return (

            // <main>
            //     <button onClick={() => setModalActive(true)}>open</button>
            //     <Modal active={modalActive} setActive={setModalActive} />
            // </main>

            <div className={style.body} >
                <Header />
                <button style={{
                    width: "50vw",
                    height: "20vh",
                    margin: "0 auto",
                    paddingTop: "10vh",
                    zIndex: 2
                }} onClick={() => setModalActive(!modalActive)}>change</button>
                <Modal active={modalActive}/>
            </div>
        );
    } else {
        window.location.replace(LOGIN_ROUTE)
    }
};



export default Home;
