import React, {useState} from 'react';
import style from "./Content.module.css";

const Content = (props) => {

    const [login, setLogin] = useState("");
    const [password, setPassword] = useState("");

    return (
        <section className={style.content}>
            <header className={style.content__header}>Все сервисы</header>
            <main className={style.content__main}>


                {

                    props.list.map(item => {

                            return (
                                <div className={style.content_item} key={item.id}>
                                    <div style={{
                                        display: "flex",
                                        justifyContent: "space-around",
                                        alignItems: "center",
                                        gap: "40px"
                                    }}>
                                        <div style={{
                                            fontSize: "30px",
                                            justifySelf: "start"
                                        }}>name:</div>
                                        <input style={{
                                            display: "flex",
                                            alignItems: "center",
                                            justifyContent: "center",
                                            fontSize: "170%",
                                            width: "100%",
                                            height: "80%",
                                            background: "#ecbf83",
                                            borderRadius:"40px",
                                            paddingLeft: "20px",
                                        }} type={"text"} placeholder={item.name} />
                                    </div>




                                    <div style={{
                                        display: "flex",
                                        justifyContent: "space-around",
                                        alignItems: "center",
                                        gap: "40px"
                                    }}>
                                        <div style={{
                                            fontSize: "30px",
                                            justifySelf: "start"
                                        }}>login:</div>
                                        <input style={{
                                            display: "flex",
                                            alignItems: "center",
                                            justifyContent: "center",
                                            fontSize: "170%",
                                            width: "100%",
                                            height: "80%",
                                            background: "#ecbf83",
                                            borderRadius:"40px",
                                            paddingLeft: "20px",
                                        }} type={"text"} placeholder={item.login} onChange={(event) => setLogin(event.target.value)} />
                                    </div>





                                    <div style={{
                                        display: "flex",
                                        justifyContent: "space-around",
                                        alignItems: "center",
                                        gap: "40px"
                                    }}>
                                        <div style={{
                                            fontSize: "30px",
                                            justifySelf: "start"
                                        }}>password:</div>
                                        <input style={{
                                            display: "flex",
                                            alignItems: "center",
                                            justifyContent: "center",
                                            fontSize: "170%",
                                            width: "100%",
                                            height: "80%",
                                            background: "#ecbf83",
                                            borderRadius:"40px",
                                            paddingLeft: "20px"
                                        }} type={"text"} placeholder={item.pass} onChange={(event) => setPassword(event.target.value)} />
                                    </div>
                                    <div style={{
                                        display: "flex",
                                        gap: "10px",
                                    }}>
                                        {/*<div style={{*/}
                                        {/*    display: "flex",*/}
                                        {/*    alignItems: "center",*/}
                                        {/*    justifyContent: "center",*/}
                                        {/*    fontSize: "170%",*/}
                                        {/*    width: "50%",*/}
                                        {/*    height: "100%",*/}
                                        {/*    background: "#ff001e",*/}
                                        {/*    borderRadius:"40px",*/}
                                        {/*    paddingLeft: "20px",*/}
                                        {/*    cursor: "pointer",*/}
                                        {/*}}>удалить</div>*/}
                                        <div style={{
                                            display: "flex",
                                            alignItems: "center",
                                            justifyContent: "center",
                                            fontSize: "170%",
                                            width: "50%",
                                            height: "100%",
                                            background: "#ef9f32",
                                            borderRadius:"40px",
                                            paddingLeft: "20px",
                                            cursor: "pointer",
                                        }} onClick={(event) => console.log({
                                            name: item.name,
                                            login: login,
                                            password: password,
                                        })}>сохранить</div>
                                    </div>
                                </div>
                            )
                        }
                    )
                }

            </main>
        </section>
    );
};

export default Content;
