import React from 'react';
import { Link } from 'react-router-dom';
import { ADVERT_PAGE_ROUTE } from '../../utils/consts';
import heart1 from "../../sourse/images/adverts/heart1.png"
import heart2 from "../../sourse/images/adverts/heart2.png"
import checked1 from "../../sourse/images/adverts/cheked1.png"
import checked2 from "../../sourse/images/adverts/checked2.png"
import "./Advert.css"
const Advert = (props) => {
    let icon1 = ""
    let icon2 = ""
    if (props.index.checked) {
        icon1 = checked2
    } else {
        icon1 = checked1
    }
    if (props.index.liked) {
        icon2 = heart2
    } else {
        icon2 = heart1
    }
    return (


        <a href="" className="group relative block h-64 sm:h-80 lg:h-96">
            <span className="absolute inset-0 border-2 border-dashed border-black"></span>

            <div
                className="relative flex h-full transform items-end border-2 border-black bg-white transition-transform group-hover:-translate-x-2 group-hover:-translate-y-2"
            >
                <div
                    className="p-4 !pt-0 transition-opacity group-hover:absolute group-hover:opacity-0 sm:p-6 lg:p-8"
                >
                    <svg
                        xmlns="http://www.w3.org/2000/svg"
                        className="h-10 w-10 sm:h-12 sm:w-12"
                        fill="none"
                        viewBox="0 0 24 24"
                        stroke="currentColor"
                    >
                        <path
                            stroke-linecap="round"
                            stroke-linejoin="round"
                            stroke-width="2"
                            d="M3.055 11H5a2 2 0 012 2v1a2 2 0 002 2 2 2 0 012 2v2.945M8 3.935V5.5A2.5 2.5 0 0010.5 8h.5a2 2 0 012 2 2 2 0 104 0 2 2 0 012-2h1.064M15 20.488V18a2 2 0 012-2h3.064M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                        />
                    </svg>

                    <h2 className="mt-4 text-xl font-medium sm:text-2xl">Go around the world</h2>
                </div>

                <div
                    className="absolute p-4 opacity-0 transition-opacity group-hover:relative group-hover:opacity-100 sm:p-6 lg:p-8"
                >
                    <h3 className="mt-4 text-xl font-medium sm:text-2xl">Go around the world</h3>

                    <p className="mt-4 text-sm sm:text-base">
                        Lorem ipsum dolor sit amet consectetur adipisicing elit. Cupiditate,
                        praesentium voluptatem omnis atque culpa repellendus.
                    </p>

                    <p className="mt-8 font-bold">Read more</p>
                </div>
            </div>
        </a>
  );
};

export default Advert;





