import React, {Component, Fragment} from 'react';
import Header from "../components/Header/Header";
import {Link} from "react-router-dom";
import FormItem from "../components/FormItem/FormItem";
import  style from "./Authorisation.css";
import {FORM_ROUTE} from "../utils/consts";

import "https://unpkg.com/ionicons@7.1.0/dist/ionicons/ionicons.esm.js";
import "https://unpkg.com/ionicons@7.1.0/dist/ionicons/ionicons.js";

export default class Authorisation extends Component {
    render() {
        const isAuthorised = true;

        return (
            <Fragment>
                <Header />
                <div className='wrapper'>
                    <Link to="/">
                        <span className={style.iconClose}>
                            <ion-icon name="close" />
                        </span>
                    </Link>
                    <div className={style.formBox}>
                        <h2 style={{textAlign: "center"}}>{isAuthorised ? 'Login' : 'Registration'}</h2>
                        <form>
                            {
                                isAuthorised && <FormItem iconName={"person"} type={"text"} labelName={"Username"}/>
                            }
                            <div className={'input-box'}>
                                <span className={'icon'}>
                                    <ion-icon name='mail'></ion-icon>
                                </span>
                                <input type='email' required />
                                <label>{'Login'}</label>
                            </div>
                            {/*<FormItem iconName={"mail"} type={"email"} labelName={"Login"}/>*/}
                            <FormItem iconName={"lock-closed"} type={"password"} labelName={"Password"} />
                            <div className={style.rememberForgot}>
                                <label>
                                    <input type="checkbox" />
                                    {
                                        isAuthorised ? 'Remember me' : 'I\'m agree to the terms & conditions'
                                    }
                                </label>
                                <a href="#">Forgot password?</a>
                            </div>
                            <Link to={FORM_ROUTE}>
                                <button type="submit" className="btn">
                                    {
                                        isAuthorised ? 'Login' : 'Register'
                                    }
                                </button>
                            </Link>
                            <div className={style.loginRegister}>

                            </div>
                        </form>
                    </div>
                </div>
            </Fragment>
        );
    }
};
