import React, {Component, Fragment} from 'react';
import Header from "./Header/Header";
import {Link} from "react-router-dom";
import FormItem from "../components/FormItem/FormItem";
import  style from "./Authorization.css";
import ""

export default class Authorisation extends Component {
    render() {
        const isAuthorised = true;

        const sendMessage = () => {
            console.log(this.value)
        }

        const submitChackin = event => {
            event.preventDefault();
            if(!validator.isEmail(register.email)) {
                alert("You did not enter email")
            } else if(register.password !== register.password2) {
                alert("Repeated password incorrectly")
            } else if(!validator.isStrongPassword(register.password, {minSymbols: 0})) {
                alert("Password must consist of one lowercase, uppercase letter and number, at least 8 characters")
            } else {
                axios.post(DOMEN_SERVER + "/auth/registration/", {
                    username: register.username,
                    email: register.email,
                    password: register.password,
                }).then(res => {
                    if (res.data === true) {
                        window.location.href = DOMEN_SITE + "/auth"
                    } else {
                        alert("There is already a user with this email")
                    }
                }).catch(() => {
                    alert("An error occurred on the server")
                })
            }
        }

        return (
            <Fragment>
                <Header />
                <div className={style.wrapper}>
                    <Link to="/">
                        <span className={style.iconClose}>
                            <ion-icon name="close" />
                        </span>
                    </Link>
                    <div className={style.formBox}>
                        <h2>{isAuthorised ? 'Login' : 'Registration'}</h2>
                        <form onSubmit={submitChackin}>
                            {
                                isAuthorised && <FormItem iconName={"person"} type={"text"} labelName={"Username"}/>
                            }
                            <FormItem iconName={"mail"} type={"email"} labelName={"Login"}/>
                            <FormItem iconName={"lock-closed"} type={"password"} labelName={"Password"} />
                            <div className={style.rememberForgot}>
                                <label>
                                    <input type="checkbox" />
                                    {
                                        isAuthorised ? 'Remember me' : 'I\'m agree to the terms & conditions'
                                    }
                                </label>
                                <a href="#">Forgot password?</a>
                            </div>
                            <Link to='/profile'>
                                <button type="submit" className="btn" onClick={sendMessage}>
                                    {
                                        isAuthorised ? 'Login' : 'Register'
                                    }
                                </button>
                            </Link>
                            <div className="login-register">
                                <p>
                                    {
                                        isAuthorised ? 'Don\'t have an account?' : 'Already have an account?'
                                    }
                                    <br/>
                                        <a href="#" className="register-link">
                                            {
                                                isAuthorised ? 'Register' : 'Login'
                                            }
                                        </a>
                                </p>
                            </div>
                        </form>
                    </div>
                </div>
            </Fragment>
        );
    }
};
