import React, {Component, Fragment, useState} from 'react';
import Header from "../components/Header/Header";
import {Link} from "react-router-dom";
import FormItem from "../components/FormItem/FormItem";
import {FORM_ROUTE} from "../utils/consts";

import "https://unpkg.com/ionicons@7.1.0/dist/ionicons/ionicons.esm.js";
import "https://unpkg.com/ionicons@7.1.0/dist/ionicons/ionicons.js";
import axios from "axios";

const Authorisation = () => {

    const [isAuth, setIsAuth] = useState(false);

    const DOMEN_SERVER = 'http://localhost:8080';

    const submitChackin = event => {
        event.preventDefault();
        axios.post(DOMEN_SERVER + "/auth/register", {
            id: register.id,
            firstName: register.firstName,
            lastName: register.lastName,
            password: register.password,
            emailID: register.emailID,
            role: register.role
        }).then(res => {
            if (res.data === true) {
                alert("True")
            } else {
                alert("Произошла ошибка")
            }
        }).catch(() => {
            alert("An error occurred on the server")
        })
    }

    const [register, setRegister] = useState(() => {
        return {
            id: null,
            firstName: "",
            lastName: "",
            password: "",
            emailID: "",
            role: "ROLE_USER"
        }
    })

    const changeInputRegister = event => {
        event.persist()
        setRegister(prev => {
            return {
                ...prev,
                [event.target.name]: event.target.value,
            }
        })
    }
        return (
            <Fragment>
                <Header />
                <div className='wrapper'>
                    <div className='form-box'>
                        <h2>{isAuth ? 'Login' : 'Registration'}</h2>
                        <form onSubmit={submitChackin}>
                            {/*{*/}
                            {/*    isAuth && <FormItem iconName={"person"} type={"text"} labelName={"Username"}/>*/}
                            {/*}*/}
                            <div className={'input-box'}>
                                <span className={'icon'}>
                                    <ion-icon name='mail'></ion-icon>
                                </span>
                                <input
                                    type="text"
                                    id="firstName"
                                    name="firstName"
                                    value={register.firstName}
                                    onChange={changeInputRegister}
                                    required />
                                <label>{'firstName'}</label>
                            </div>
                            <div className={'input-box'}>
                                <span className={'icon'}>
                                    <ion-icon name='mail'></ion-icon>
                                </span>
                                <input
                                    type="text"
                                    id="lastName"
                                    name="lastName"
                                    value={register.lastName}
                                    onChange={changeInputRegister}
                                    formNoValidate
                                    required />
                                <label>{'lastName'}</label>
                            </div>
                            <div className={'input-box'}>
                                <span className={'icon'}>
                                    <ion-icon name='mail'></ion-icon>
                                </span>
                                <input
                                    type="email"
                                    id="emailID"
                                    name="emailID"
                                    value={register.emailID}
                                    onChange={changeInputRegister}
                                    required />
                                <label>{'email'}</label>
                            </div>
                            <div className={'input-box'}>
                                <span className={'icon'}>
                                    <ion-icon name='mail'></ion-icon>
                                </span>
                                <input
                                    type="password"
                                    id="password"
                                    name="password"
                                    value={register.password}
                                    onChange={changeInputRegister}
                                    required />
                                <label>{'password'}</label>
                            </div>
                            {/*<FormItem iconName={"mail"} type={"email"} labelName={"Login"}/>*/}
                            {/*<FormItem iconName={"lock-closed"} type={"password"} labelName={"Password"} />*/}
                            <div>
                                <label>
                                    <input type="checkbox" />
                                    {
                                        isAuth ? 'Remember me' : 'I\'m agree to the terms & conditions'
                                    }
                                </label>
                                <a href="#">Forgot password?</a>
                            </div>
                            <Link to={FORM_ROUTE}>
                                <button type="submit" className="btn" >
                                    {
                                        isAuth ? 'Login' : 'Register'
                                    }
                                </button>
                            </Link>
                        </form>
                    </div>
                </div>
            </Fragment>
        );
}

export default Authorisation
