import React from 'react';
import { Link } from 'react-router-dom';
import { ADVERT_PAGE_ROUTE } from '../../utils/consts';
import heart1 from "../../sourse/images/adverts/heart1.png"
import heart2 from "../../sourse/images/adverts/heart2.png"
import checked1 from "../../sourse/images/adverts/cheked1.png"
import checked2 from "../../sourse/images/adverts/checked2.png"
import "./Advert.css"
const Advert = (props) => {
    let icon1 = ""
    let icon2 = ""
    if (props.index.checked) {
        icon1 = checked2
    } else {
        icon1 = checked1
    }
    if (props.index.liked) {
        icon2 = heart2
    } else {
        icon2 = heart1
    }
    return (


        <a href="" className="group relative block h-64 sm:h-80 lg:h-96">
            <span className="absolute inset-0 border-2 border-dashed border-black"></span>

            <div
                className="relative flex h-full transform items-end border-2 border-black bg-white transition-transform group-hover:-translate-x-2 group-hover:-translate-y-2"
            >
                <div
                    className="p-4 !pt-0 transition-opacity group-hover:absolute group-hover:opacity-0 sm:p-6 lg:p-8"
                >
                    1
                </div>

                <div
                    className="absolute p-4 opacity-0 transition-opacity group-hover:relative group-hover:opacity-100 sm:p-6 lg:p-8"
                >
                    2
                </div>
            </div>
        </a>
  );
};

export default Advert;





