import React, {Fragment} from 'react';
import {Link} from "react-router-dom";

import "https://unpkg.com/ionicons@7.1.0/dist/ionicons/ionicons.esm.js";
import "https://unpkg.com/ionicons@7.1.0/dist/ionicons/ionicons.js";

import style from "./temp/style.css"

const Content = () => {

    const FormItem = () => {
        return (
            <div className={'input-box'}>
                <span className={'icon'}>
                    <ion-icon name={this.iconName}></ion-icon>
                </span>
                <input type={this.type} required />
                <label>{this.labelName}</label>
            </div>
        );
    }

    const Form = () => {
        return (
            <Fragment>
                <div>
                    <Link to="/">
                        <div className='icon-close'>
                            <ion-icon name="close" />
                        </div>
                    </Link>
                    <div className='wrapper'>

                        <div className='form-box'>
                            <h2>{this.state.isAuthorised ? 'Login' : 'Registration'}</h2>
                            <form>
                                {
                                    this.state.isAuthorised && <FormItem iconName={"person"} type={"text"} labelName={"Username"}/>
                                }
                                <div className={'input-box'}>
                                <span className={'icon'}>
                                    <ion-icon name='mail'></ion-icon>
                                </span>
                                    <input type='email' required />
                                    <label>{'Login'}</label>
                                </div>
                                {/*<FormItem iconName={"mail"} type={"email"} labelName={"Login"}/>*/}
                                <FormItem iconName={"lock-closed"} type={"password"} labelName={"Password"} />
                                <div className={style.rememberForgot}>
                                    <label>
                                        <input type="checkbox" />
                                        {
                                            this.state.isAuthorised ? 'Remember me' : 'I\'m agree to the terms & conditions'
                                        }
                                    </label>
                                    <a href="#">Forgot password?</a>
                                </div>
                                <Link to={FORM_ROUTE}>
                                    <button type="submit" className="btn" >
                                        {
                                            this.state.isAuthorised ? 'Login' : 'Register'
                                        }
                                    </button>
                                </Link>
                                <div className={style.loginRegister}>

                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </Fragment>
        );
    }

    return (
        <div style={{
            width: "100%",
            height: "946px",
            // background: "red"
        }}>

        </div>
    );
};

export default Content;
