import React from 'react';
import Header from "../../components/Header/Header";
import Aside from "../../components/Asside/Aside";
import MinHeader from "../../components/MiniHeader/MinHeader";
import Footer from "../../components/Footer/Footer";
import Advert from '../../components/Advert/Advert';
import Sidebar from '../../components/SideBar/SideBar';

const listOfAdverts = [
    {
        type: "Продажа",
        url: "https://www.imufa.ru/images/2019/03/17/fotka3.jpg"
    }
]

const Home = () => {
    return (
    <div className='content-center'>
        <Header />
        <div className="flex" style={{"min-width": "1722px",
  "max-width": "2500px", width: "min-contend", margin: "0 auto"}}>
            <Sidebar />
            {/*<MinHeader />*/}
            <div className='flex flex-wrap border-r-indigo-700 border-l-indigo-700 border-l border-r'>
                {
                    listOfAdverts.map(index => {
                        return <Advert key={index}/>
                    })

                }
            </div>
        </div>
        <Footer />
    </div>
  );
};

export default Home;
