import React, {Fragment, useState} from "react";
import axios from "axios";
import {Link} from "react-router-dom";
import "./SignIn.css";
import {VERIFICATION_ROUTE} from "../../utils/consts";

const Login = () => {

    function randomInteger(min, max) {
        let rand = min + Math.random() * (max - min);
        return Math.round(rand);
    }
    let rez = ""
    for (let i = 0; i < 6; i++) {
        rez += randomInteger(0, 9);
    }

    let structure = {
        Mail: "",
        Firstname: "",
        Lastname: "",
        Password1: "",
        Password2: ""
    }

    const log = (e) => {
        e.preventDefault()
        axios.post(
            "http://localhost:8080/send",
            {
                "recipient":structure,
                "code": rez
            }
        ).then(res => {
            if (res) {

                return window.location.replace("http://localhost:3000/verification");
            }
        }).catch(err => {
            console.log(structure)
        })
    }

    let str = ""

    return (
        <Fragment>
            <div>
                <div className="wrapper1">
                    <Link to="/">
                        <div className='icon-close'>
                            <ion-icon name="close" />
                        </div>
                    </Link>
                    <div className="form-box">
                        <h2>Войти</h2>
                        {/*<form>*/}

                        <div className="input-box">
                            <span className={'icon'}>
                                <ion-icon name={"magic"}></ion-icon>
                            </span>
                            <input type="text" onChange={(e) => {
                                str = e.target.value
                                structure.Mail = str
                            }}/>
                            <label>Mail</label>
                        </div>

                        <div className="input-box">
                            <span className={'icon'}>
                                <ion-icon name={"magic"}></ion-icon>
                            </span>
                            <input type="password" onChange={(e) => {
                                str = e.target.value
                                structure.Password2 = str
                            }}/>
                            <label>Password2</label>
                        </div>

                        <Link to={VERIFICATION_ROUTE}>
                            <button type="submit" className="btn" onClick={(e) => log(e)}>
                                Войти
                            </button>
                        </Link>
                        {/*</form>*/}
                    </div>
                </div>
            </div>
        </Fragment>
    );
}

export default Login;
