import UploadFilesService from "../services/ upload-files.service";
import React, {Component} from "react";
import "bootstrap";
import "./UploadFiles.css";
export default class UploadFiles extends Component {
  constructor(props) {
    super(props);

    this.state = {
      selectedFiles: undefined,
      currentFile: undefined,
      progress: 0,
      message: "",

      fileInfos: [],
    };
  }

  selectFile(event) {
    this.setState({
      selectedFiles: event.target.files,
    });
  }

  upload() {
    let currentFile = this.state.selectedFiles[0];

    this.setState({
      progress: 0,
      currentFile: currentFile,
    });

    UploadFilesService.upload(currentFile, (event) => {
      this.setState({
        progress: Math.round((100 * event.loaded) / event.total),
      });
    })
      .then((response) => {
        this.setState({
          message: response.data.message,
        });
        return UploadFilesService.getFiles();
      })
      .then((files) => {
        this.setState({
          fileInfos: files.data,
        });
      })
      .catch(() => {
        this.setState({
          progress: 0,
          message: "Could not upload the file!",
          currentFile: undefined,
        });
      });

    this.setState({
      selectedFiles: undefined,
    });
  }

  componentDidMount() {
    UploadFilesService.getFiles().then((response) => {
      this.setState({
        fileInfos: response.data,
      });
    });
  }

  render() {
    const {
      selectedFiles,
      currentFile,
      progress,
      message,
      fileInfos,
    } = this.state;

    return (
      <div>
        {currentFile && (
          <div className="progress">
            <div
              className="progress-bar progress-bar-info progress-bar-striped bg-purple-700 progress-bar-animated"
              role="progressbar"
              aria-valuenow={progress}
              aria-valuemin="0"
              aria-valuemax="100"
              style={{ width: progress + "%" }}
            >
              {/* {progress}% */}
            </div>
          </div>
        )}

        <label className="btn btn-default">
          <input className="bg-transparent" type="file" onChange={this.selectFile} />
        </label>
        <div class="input__wrapper">
          <input name="file" type="file" id="input__file" class="input input__file" multiple />
          <label for="input__file" class="input__file-button">
            <span class="input__file-icon-wrapper">
              <img class="input__file-icon"  src="./img/add.svg" alt="Выбрать файл" width="25" />
              </span>
            <span class="input__file-button-text">Выберите файл</span>
          </label>
        </div>

        <button className="btn btn-success"
          disabled={!selectedFiles}
          onClick={this.upload}
        >
          Upload
        </button>
      </div>
    );
  }
}
