import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer";
import "https://unpkg.com/ionicons@7.1.0/dist/ionicons/ionicons.esm.js";
import "https://unpkg.com/ionicons@7.1.0/dist/ionicons/ionicons.js";
import "./Authorisation.css"
import React, {Fragment, useState} from "react";
import axios from "axios";
import {Link} from "react-router-dom";
import {HOME_ROUTE, LOGIN_ROUTE, VERIFICATION_ROUTE} from "../../utils/consts";
const SignIn = () => {

    const [Mail, setMail] = useState('');
    const [Password, setPassword] = useState('');


    function getRand() {
        let rez = ""
        for (let i = 0; i < 6; i++) {
            rez += Math.round(0 + Math.random() * (9 - 0));
        }
    }

    const code = getRand();

    const log = (e) => {
        e.preventDefault()
        axios.post(
            "http://localhost:8080/send",
            {
                "recipient": {
                    Mail,
                    Password
                },
                "code": code
            }
        ).then(res => {
            if (Mail === '1' && Password === '2') {
                return window.location.replace("http://localhost:3000/verification");
            }
        }).catch(err => {
            // console.log(err)
        })
    }

    const InputBox = (props) => {

        const { iconName, labelName, setState } = props

        return(
            <div className="input-box">
                <span className={'icon'}>
                    <ion-icon name={iconName}></ion-icon>
                </span>
                <input type="text" onChange={(e) => {
                     setState(e.target.value)
                }}/>
                <label>{labelName}</label>
            </div>
        );
    }

    return (
        <div>
            <Header />
            <div style={{
                width: "100%",
                height: "946px",
                display: "flex",
                alignItems: "center",
                justifyContent: "center"
            }}>

                <Fragment>
                    <div>
                        <div className="wrapper1">
                            <Link to={HOME_ROUTE}>
                                <div className='icon-close'>
                                    <ion-icon name="close" />
                                </div>
                            </Link>
                            <div className="form-box">
                                <h2>Войти</h2>
                                {/*<form className="text-center">*/}

                                    <InputBox iconName={"mail"} labelName={"Mail"} setState={setMail()} />
                                    {/*<InputBox iconName={"password"} labelName={"Password"} />*/}
                                    {/*<div className="input-box">*/}
                                    {/*    <span className={'icon'}>*/}
                                    {/*        <ion-icon name={"mail"}></ion-icon>*/}
                                    {/*    </span>*/}
                                    {/*    <input type="text" onChange={(e) => {*/}
                                    {/*        setMail(e.target.value)*/}
                                    {/*    }}/>*/}
                                    {/*    <label>{"Mail"}</label>*/}
                                    {/*</div>*/}

                                    {/*<div className="input-box">*/}
                                    {/*    <span className={'icon'}>*/}
                                    {/*        <ion-icon name={"password"}></ion-icon>*/}
                                    {/*    </span>*/}
                                    {/*    <input type="password" onChange={(e) => {*/}
                                    {/*        setPassword(e.target.value)*/}
                                    {/*    }}/>*/}
                                    {/*    <label>{"Password"}</label>*/}
                                    {/*</div>*/}
                                    <Link to={LOGIN_ROUTE}>
                                        Зарегистрироваться
                                    </Link>
                                    {/*<Link to={VERIFICATION_ROUTE}>*/}
                                        <button type="submit" className="btn" onClick={console.log(Mail)}>
                                            Войти
                                        </button>
                                    {/*</Link>*/}
                                {/*</form>*/}
                            </div>
                        </div>
                    </div>
                </Fragment>
            </div>
            <Footer />
        </div>
    );
};

export default SignIn;
