import React, {Fragment, useState} from "react";
import axios from "axios";
import {Link} from "react-router-dom";
import style from "./Login.module.css";
import {VERIFICATION_ROUTE} from "../../utils/consts";

const Login = () => {

    const [dataRegister, setDataRegister] = useState({
        "email" : "",
        "Firstname" : "",
        "Lastname" : "",
        "Password1" : "",
        "Password2" : "",
    })

    const [dataLogin, setDataLogin] = useState({
        "email" : "",
        "Password" : "",
    })

    const handleChange = (e) => {
        const value = e.target.value;
        setDataLogin({...dataLogin, [e.target.name]: value})
    };

    const x = () => {
        console.log(dataLogin)
    }

    const [isAuthorised, setIsAuthorised] = useState(false)

    // const random = () => {
    //     function randomInteger(min, max) {
    //         let rand = min + Math.random() * (max - min);
    //         return Math.round(rand);
    //     }
    //     let rez = ""
    //     for (let i = 0; i < 6; i++) {
    //         rez += randomInteger(0, 9);
    //     }
    //     axios.post(
    //         "http://localhost:8080/send",
    //         {
    //             "recipient" : "ustavitski.andrei@gmail.com",
    //             "code" : rez
    //         }
    //     ).then(response => {
    //         if (response) {
    //             axios.post(
    //                 "http://localhost:8080/auth/register",
    //                 {
    //                     "firstName" : "Vazgen",
    //                     "lastName" : "Karapetyan",
    //                     "password" : "1111",
    //                     "emailID" : "7648756"
    //                 }
    //             ).then(respon => {
    //                 console.log(respon)
    //             })
    //         }
    //     })
    // }

    const tog = () => {
        setIsAuthorised(!isAuthorised)
    }

    const FormItem = (props) => {

        const { iconName, labelName, type } = props

        return (
            <div className={style.inputBox}>
                <span className={'icon'}>
                    <ion-icon name={iconName}></ion-icon>
                </span>
                <input type={type} onChange={(e) => (handleChange(e))} />
                <label>{labelName}</label>
            </div>
        );
    }

    return (
        <Fragment>
            <div>
                <div className={style.wrapper}>
                    <Link to="/" className="flex justify-content-end">
                        <div className='icon-close'>
                            <ion-icon name="close" />
                        </div>
                    </Link>
                    <div className={style.formBox}>
                        <h2 onClick={tog}>{isAuthorised ? 'Войти' : 'Зарегистрироваться'}</h2>
                        <form>
                            {/*<FormItem iconName={"mail"} type={"email"} labelName={"Mail"}/>*/}
                            <div className={style.inputBox}>
                                <span className={'icon'}>
                                    <ion-icon name={"mail"}></ion-icon>
                                </span>
                                <input
                                    type="text"
                                    name='firstName'
                                    value={dataLogin.email}
                                    onChange={(e) => handleChange(e)}
                                />
                                <label>{1}</label>
                            </div>
                            { !isAuthorised && <FormItem iconName={"person"} type={"text"} labelName={"Firstname"}/> }
                            { !isAuthorised && <FormItem iconName={"person"} type={"text"} labelName={"Lastname"}/> }
                            { !isAuthorised && <FormItem iconName={"lock-closed"} type={"password"} labelName={"Password"} /> }
                            {/*<FormItem iconName={"lock-closed"} type={"password"} labelName={"Password"} />*/}
                            <div className={style.inputBox}>
                                <span className={'icon'}>
                                    <ion-icon name={"mail"}></ion-icon>
                                </span>
                                <input
                                    type="text"
                                    name='secondName'
                                    value={dataLogin.Password}
                                    onChange={(e) => handleChange(e)}
                                />
                                <label>{1}</label>
                            </div>
                            <Link to={VERIFICATION_ROUTE}>
                                <button type="submit" className={style.btn} onClick={x}>
                                    {
                                        isAuthorised ? 'Войти' : 'Зарегистрироваться'
                                    }
                                </button>
                            </Link>
                            <div className={style.loginRegister} />
                        </form>
                    </div>
                </div>
            </div>
        </Fragment>
    );
}

export default Login;
