import React, { useState } from 'react';
import style from "./Home.module.css";
import {ERROR_ROUTE, LOGIN_ROUTE} from "../../utils/consts";
import {deleteCookie, getCookie, setCookie} from "../../utils/cookie";
import axios from "axios";
import Header from "../../components/Header/Header";
const Home = (props) => {

    let l = []

    console.log(getCookie("jwt"))

    if (getCookie("jwt")) {
        axios.get(
            "http://localhost:8080/developer/valid",
            {headers: {
                    'Authorization': 'Bearer ' + getCookie("jwt") //the token is a variable which holds the token
                }}
        ).then(
            axios.get(
                "http://localhost:8080/data/list",
                {headers: {
                        'Authorization': 'Bearer ' + getCookie("jwt") //the token is a variable which holds the token
                    }}
            ).then(
                res => {
                    for (let i of res.data) {
                        l.push(i)
                    }
                }
            )
        ).catch(
            err => {
                deleteCookie("jwt")
                window.location.replace(LOGIN_ROUTE)
            }
        )
        return (
            <div className={style.body}>
                <div className="flex justify-center" >
                    <div className='w-full'>
                        <div className='flex flex-wrap justify-center' style={{margin: "0 auto",paddingTop: "0px", maxWidth: "1460px", minWidth: "300px"}}>
                            <Header />
                            <div>
                                {
                                    l.map(item => {
                                        <div>item</div>
                                    })
                                }
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    } else {
        window.location.replace(LOGIN_ROUTE)
    }
};



export default Home;
