    //                 "password": password1
    //             }
    //         ).then(res => {
    //             if (res) {
    //                 // sendData();
    //                 // return window.location.replace()
    //             }
    //         }).catch(() => {
    //             alert("Некорректная почта!")
    //         })
    //     } else {
    //       alert("Пароли не совпадают!")
    //     }
    // }

    // const sendLogin = (e) => {
    //     e.preventDefault()
    //     axios.post(
    //         __URL__ + "login",
    //         {
    //             "login": login,
    //             "password": password1
    //         }
